configfiles.configfile:
use Switch;

# Read in files
$infile="${inp_gt}";
$pedfilepro="${inp_ped}";

# Override Illumina map
$chip="Illumina";
$genmap="Other";
$other_map_illumi="${inp_map}";

# Override Caucasian MAF on input
if "${inp_maf}" == "":
    $allfreq="Equal"
else:
    $allfreq="Caucasian";
    $freq_cauc_illumi="${inp_maf}";

$del_errors=0;

$skip_notinf=0;
$del_errors=1;
$del_unlikely=1;

# The likelihood of a user injecting perl code via HTML input is so low...
# &"${inp_prog}"
#
# But better to be safe:
switch ("${inp_prog}") {
   case "Allegro"    { &Allegro;   }
   case "Genehunter" { &Genehunter;}
   case "Merlin"     { &Merlin;    }
   case "Mega2"      { &Mega2;     }
}
